mapper.update:
UPDATE post
SET title = #{title}, content = #{content}, status = #{status}, modified_date = #{modifiedDate}, center_id = #{centerId}, exercise_type = #{exerciseType}, price = #{price}, gender = #{gender}, recruitment_num = #{recruitmentNum} 
WHERE post_id = #{postId} ;

mapper.select:
SELECT *
FROM post 
ORDER BY created_date DESC;
