mapper.select:
SELECT post.*, user.nickname, sports_center.road_address
FROM post
INNER JOIN user
ON post.user_id = user.user_id
INNER JOIN sports_center
ON post.center_id = sports_center.center_id


        AND post.status = #{status}


        AND post.gender = #{gender}


        AND post.exercise_type LIKE CONCAT('%', #{exerciseType}, '%')


        AND post.price >= #{minPrice}


        AND post.price <= #{maxPrice}

SELECT post.*, user.nickname, sports_center.road_address FROM post INNER JOIN user ON post.user_id = user.user_id INNER JOIN sports_center ON post.center_id = sports_center.center_id WHERE post.center_id IN (SELECT center_id FROM sports_center WHERE (6371*acos(cos(radians(#{latitude}))*cos(radians(latitude))*cos(radians(longitude)-radians(#{longitude}))+sin(radians(#{latitude}))*sin(radians(latitude)))) <= 1)
AND post.status = #{status}
AND post.gender = #{gender}
AND post.exercise_type LIKE CONCAT('%', #{exerciseType}, '%')
AND post.price >= #{minPrice}
AND post.price <= #{maxPrice}
